const initTreshold = () => {
    document.getElementById("gas_treshold").addEventListener("change", function () {
        const priceVal = document.getElementById("gas_treshold").value;
        chrome.storage.local.set({
            "gas_treshold": priceVal
        }, function () {
            console.log('New treshold set', priceVal);
        });
    });

    document.getElementById("do_beep").addEventListener("change", function () {
        const doBeep = document.getElementById("do_beep").checked;
        chrome.storage.local.set({
            "gas_beep": doBeep
        }, function () {
            console.log('New beep value set', doBeep);
        });
        if (!doBeep) {
            clearInterval(beepTimer);
            beepTimer = undefined;
        }
    });

    chrome.storage.local.get(["gas_treshold"], (res) => {
        const newTreshold = res['gas_treshold'] !== undefined ? res['gas_treshold'] : 150;
        document.getElementById("gas_treshold").value = newTreshold;
        console.log('New treshold get', newTreshold);
    })

    chrome.storage.local.get(['gas_beep'], (res) => {
        const doBeep = res['gas_beep'] !== undefined ? res['gas_beep'] : false;
        document.getElementById("do_beep").checked = doBeep;
    });    
}


const handleMessagePrices = () => {
    chrome.runtime.onMessage.addListener(({
        array
    }, messageSender, sendResponse) => {
        console.log(messageSender.tab ?
            "from a content script:" + messageSender.tab.url :
            "from the extension");
        setGasPrices(array);
        beep();
        return true;
    })
}

const setGasPrices = (array) => {
    const prices = {
        standard: array[0],
        fast: array[1],
        rapid: array[2],
        fantom: array[3]
    }

    document.getElementById("fantom-standard").innerHTML = `${prices.standard} Gwei`;
    document.getElementById("fantom-fast").innerHTML = `${prices.fast} Gwei`;
    document.getElementById("fantom-rapid").innerHTML = `${prices.rapid} Gwei`;
    document.getElementById("fantom-price").innerHTML = `$${prices.fantom}`;
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

const handleBeep = (actGasPrice) => {
    const doBeep = document.getElementById("do_beep").checked;
    const gasTreshold = document.getElementById("gas_treshold").value;
    if (doBeep) {
        if (actGasPrice <= gasTreshold) {
            if (!beepTimer) {
                beepTimer = setInterval(beep, 2 *1000);
            }
        }
    } else {
        clearInterval(beepTimer);
        beepTimer = undefined;
    }
}

chrome.storage.onChanged.addListener((changes, areaName) => {
    if (areaName === 'local' && changes.array) {
        const newPrices = changes.array.newValue;
        setGasPrices(newPrices);
        handleBeep(newPrices[0]);
    }
})